import MOCK_DATA from '../data/MOCK_DATA.json'

export const pedirDatos = () => {
    return new Promise((resolve, reject) =>{
        setTimeout ( () => {             
            resolve(MOCK_DATA)
        }, 3000)
    })
}

export const pedirItemPorId = (id) => {
    return new Promise((resolve, reject) =>{
        setTimeout (() =>{
            const item = MOCK_DATA.find((el) => el.id === id)
            resolve(item)
        },2000)
    })
}
