import * as React from 'react';
import { useEffect, useState } from 'react';
import stock from '../data/MOCK_DATA.json'


export const ItemListContainer = () => {

    const [productos, setProductos] = useState([])

    const pedirDatos = () =>{
        return new Promise((resolve, reject) =>{
            setTimeout ( () => {             
                resolve(stock)
            }, 1500)
        })
    }

    console.log(productos)

    useEffect(()=>{
     pedirDatos(true)
        .then((res) =>{
            setProductos(res)
        })
        .catch((err) =>{
            console.log (err)
        })
    }, [])


    return (
        <div>
            <h2>Productos</h2>
            <hr/>
            {
                productos.length > 0 &&
                <div>
                <img src={productos[0].image}/>
                <h3>{productos[0].name}</h3>
                <p>Descripci√≥n: {productos[0].description}</p>
                <p>Precio: $ {productos[0].price}</p>
            </div>
            }

        </div>

    )
}

export default ItemListContainer
